import React from "react"; 
import { Carousel } from 'react-carousel-minimal';
import ap1 from  "/Users/zac/react-portfolio/src/img/aurora/ap1.png"
import ap2 from '../img/aurora/ap2.png'

function ImageSlider() {
 const data = [
    {
      image: ap1,
      caption: "San Francisco"
    },
    {
      image: ap2,
      caption: "Scotland"
    },
    {
      image: "/Users/zac/react-portfolio/src/img/aurora/ap4.png",
      caption: "Darjeeling"
    },
    {
      image: "/Users/zac/react-portfolio/src/img/aurora/ap5.png",
      caption: "San Francisco"
    },
    {
      image: "/Users/zac/react-portfolio/src/img/aurora/ap7.png",
      caption: "Scotland"
    }
  ];

  const captionStyle = {
    fontSize: '2em',
    fontWeight: 'bold',
  }
  const slideNumberStyle = {
    fontSize: '20px',
    fontWeight: 'bold',
  }
  return (
    <div className="App">
      <div style={{ textAlign: "center" }}>
        <h2>React Carousel Minimal</h2>
        <p>Easy to use, responsive and customizable carousel component for React Projects.</p>
        <div style={{
          padding: "0 20px"
        }}>
          <Carousel
            data={data}
            time={2000}
            width="850px"
            height="500px"
            captionStyle={captionStyle}
            radius="10px"
            slideNumber={true}
            slideNumberStyle={slideNumberStyle}
            captionPosition="bottom"
            automatic={false}
            dots={true}
            pauseIconColor="black"
            pauseIconSize="40px"
            slideBackgroundColor="darkgrey"
            slideImageFit="cover"
            thumbnails={true}
            thumbnailWidth="100px"
            showNavBtn={true}
            style={{
              textAlign: "center",
              maxWidth: "850px",
              margin: "40px auto",
            }}
          />
        </div>
      </div>
    </div>
  );
}

export default ImageSlider;
