import React, { useState } from 'react';

const ImageSlider = ({ slides, imageScale = 100 }) => {
  const [current, setCurrent] = useState(0);

  const handlePrev = () => {
    setCurrent(prev => (prev <= 0 ? slides.length - 1 : prev - 1));
  };

  const handleNext = () => {
    setCurrent(prev => (prev >= slides.length - 1 ? 0 : prev + 1));
  };

  const imageSizeStyle = {
    width: `${imageScale}%`,
    height: 'auto' // Maintain aspect ratio
  };

  return (
    <div className="slider">
      {slides.length > 1 && (
        <button className="slider-btn slider-btn-prev" onClick={handlePrev}>&lt;</button>
      )}
      <img 
        src={slides[current].url} 
        alt={`Slide ${current}: ${slides[current].title}`} 
        className="slider-img" 
        style={imageSizeStyle}
      />
      {slides.length > 1 && (
        <button className="slider-btn slider-btn-next" onClick={handleNext}>&gt;</button>
      )}
    </div>
  );
};
