import React, { useState } from 'react';
import './ImageSlider.css';

const ImageSlider = ({ images, ima }) => {
  const [current, setCurrent] = useState(0);

  const handlePrev = () => {
    setCurrent(prev => (prev <= 0 ? images.length - 1 : prev - 1));
  };

  const handleNext = () => {
    setCurrent(prev => (prev >= images.length - 1 ? 0 : prev + 1));
  };

  // Calculate the width and height styles based on imageScale
  const imageSizeStyle = {
    width: `${imageScale}%`,
    height: 'auto' // Maintain aspect ratio
  };

  // Only render the slider if there are images
  if (!images || images.length === 0) {
    return null;
  }

  return (
    <div className="slider">
      <button className="slider-btn slider-btn-prev" onClick={handlePrev}>&lt;</button>
      <img 
        src={slides[current].url} 
        alt={`Slide ${current}: ${slides[current].title}`} 
        className="slider-img" 
        style={imageSizeStyle}
      />
      <button className="slider-btn slider-btn-next" onClick={handleNext}>&gt;</button>
    </div>
  );
};

// Set default props for images to be an empty array
ImageSlider.defaultProps = {
  images: []
};

export default ImageSlider;
