import React from "react";

const ImageSlider = ({ slides }) => {
    const [currentIndex, setCurrentIndex] = React.useState(0);

    const [imageDimensions, setImageDimensions] = React.useState({ width: '500px', height: '300px' });

    React.useEffect(() => {
        const image = new Image();
        image.src = slides[currentIndex].url;

        image.onload = function() {
            setImageDimensions({ width: `${this.width}vw/4`, height: `${this.height}vh/2` });
        }
    }, [currentIndex, slides]);


    const slideStyles = {
        width: imageDimensions.width,
        height: imageDimensions.height,
        position: 'relative',
        backgroundPosition: 'center',
        backgroundSize: 'cover',  
        backgroundImage: `url(${slides[currentIndex].url})`,
        backgroundRepeat: 'no-repeat',
    };

    const arrowStyles = {
        position: 'absolute',
        top: '50%',
        transform:"translateY(-50%)",
        fontSize: '45px',
        color: 'red',
        zIndex: '1',
        cursor: 'pointer',
        opacity: '0.7',
        transition: 'opacity 0.3s'
    };

    const dotsContainerStyles = {
        display: 'flex',
        justifyContent: 'center',
        position: 'absolute',
        bottom: '10px',
        width: '100%'
    };
    const dotStyles = isActive => ({
        margin: '0 3px',
        cursor: 'pointer',
        fontSize: '20px',
        color: isActive ? '#0078ff' : '#aaa'
    });

    const goToPrevious = () => {
        const isFirstSlide = currentIndex === 0;
        const newIndex = isFirstSlide ? slides.length - 1 : currentIndex - 1;
        setCurrentIndex(newIndex);
    };

    const goToNext = () => {
        const isLastSlide = currentIndex === slides.length - 1;
        const newIndex = isLastSlide ? 0 : currentIndex + 1;
        setCurrentIndex(newIndex);
    };

    const goToSlide = (slideIndex) => {
        setCurrentIndex(slideIndex);
    };

    return (
        <div style={{ position: 'relative', height: '100%', backgroundColor: 'grey' }}>
            <div style={{ ...arrowStyles, left: '32px' }} onClick={goToPrevious} onMouseEnter={e => e.target.style.opacity = '1'} onMouseLeave={e => e.target.style.opacity = '0.7'}>←</div>
            <div style={{ ...arrowStyles, right: '32px' }} onClick={goToNext} onMouseEnter={e => e.target.style.opacity = '1'} onMouseLeave={e => e.target.style.opacity = '0.7'}>→</div>
            <div style={slideStyles}></div>
            <div style={dotsContainerStyles}>
                {slides.map((_, slideIndex) => (
                    <span key={slideIndex} style={dotStyles(slideIndex === currentIndex)} onClick={() => goToSlide(slideIndex)}>
                        ●
                    </span>
                ))}
            </div>
        </div>
    );
};

export default ImageSlider;
