import React, { useState } from 'react';
import SwipeableViews from 'react-swipeable-views';
import { autoPlay } from 'react-swipeable-views-utils';
import './ImageSlider.css';

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

const ImageSlider = ({ slides, imageScale = 100 }) => {
  const [current, setCurrent] = useState(0);

  const handleStepChange = (step) => {
    setCurrent(step);
  };

  const handleClick = () => {
    // Advance to the next slide when the image is clicked or touched
    const nextIndex = (current + 1) % slides.length;
    setCurrent(nextIndex);
  };

  return (
    <div className="slider">
      <AutoPlaySwipeableViews 
        index={current}
        onChangeIndex={handleStepChange}
        enableMouseEvents // to make it swipeable with a mouse for desktop users
      >
        {slides.map((slide, index) => (
          <div key={index} onClick={handleClick}> {/* Here we handle the click or touch */}
            <img 
              src={slide.url} 
              alt={`Slide ${index}: ${slide.title}`} 
              className="slider-img" 
              style={{ width: `${imageScale}%`, height: 'auto' }} 
            />
          </div>
        ))}
      </AutoPlaySwipeableViews>
      <div className="slider-pagination">
        {slides.map((_, index) => (
          <span 
            key={index}
            className={`slider-dot ${index === current ? 'active' : ''}`}
            onClick={() => setCurrent(index)}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default ImageSlider;
